    public static int registerValidation(String user, String pass, String rePass, String email) {
        List<Users> res = DatabaseClass.selectQueryUsersLists();
        int errorIndicator = 0;
        Boolean validity = true; 
        for (Users u : res) {
            if (u.getEmail().equalsIgnoreCase(email)) {
                validity = false;
                errorIndicator = 4; // email already exists
            }
            if (u.getUsername().equalsIgnoreCase(user)) {
                validity = false;
                errorIndicator = 3; // username already taken
            }
        }
        if (!VALID_EMAIL_REGEX.matcher(email).matches()) {
            validity = false;
            errorIndicator = 1; //email is invalid
        }
        if (!pass.equals(rePass) || pass.isEmpty() || rePass.isEmpty()) {
            validity = false;
            errorIndicator = 2; //password is invalid
        }
        System.out.println(errorIndicator + " " + validity);
        if (validity == true && email.endsWith("@netducation.id")) {
            DatabaseClass.insertQuery(user, encrypting(pass,seed), email, 1);
            errorIndicator = 5; //all valid, ADMIN created
        }
        else if (validity == true) {
            DatabaseClass.insertQuery(user, encrypting(pass,seed), email, 2);
            errorIndicator = 0; //all valid, user created
        }
        return errorIndicator;
    }
    
